mapper.select:
/* CmsAccountMng.selectAdminIdCheck */
	SELECT
			count(1)
	FROM
			site_account
	
	AND			id								= #{id}

mapper.update:
/* CmsAccountMng.updateAccountData */
	UPDATE site_account SET
				use_at							= #{paramKey1},
				
				pw								= #{paramKey3},
				
				name							= #{paramKey4},
				account							= #{paramKey20},
				site_code						= #{paramKey21}
	
	AND			no								= #{idx}
