mapper.select:
/* StaticData.selectSiteCodeDataList */ SELECT T1.site_code, T1.site_txt, T1.site_etc, T1.lang_code FROM cms_site_code T1
AND T1.delete_at = 'N'
ORDER BY T1.sort ASC

/* StaticData.selectLangCodeDataList */ SELECT T1.lang_code, T1.lang_txt, T1.lang_etc FROM cms_site_language T1
AND T1.delete_at = 'N'
#{item}
ORDER BY T1.sort ASC

/* StaticData.selectMaxMenuDepth */
	SELECT
		IFNULL(MAX(depth), 0) AS depth
	FROM
		cms_menu T1
	
	AND			T1.delete_at					= 'N'
	AND			T1.use_gbn						= #{gubun}
	AND			T1.site_code					= #{site}
	AND			T1.lang_code					= #{lang}

/* StaticData.selectSeparateMenuDataList */ SELECT T1.no, T1.use_at, T1.gnb_view_at, T1.session_at, T1.tab_at, T1.menu_title, T1.menu_type, T1.uri, T1.link_type, T1.link_url, T1.board_code, T1.skin, T1.parent, T1.depth, T1.sort, T1.cms_at, (SELECT COUNT(1) FROM cms_menu T2 WHERE T1.no = T2.parent AND delete_at = 'N') AS children FROM cms_menu T1
AND T1.delete_at = 'N' AND T1.use_gbn = #{gubun} AND T1.site_code = #{site} AND T1.lang_code = #{lang} AND T1.depth = #{depth}
ORDER BY
T1.parent
)
ASC, T1.sort ASC
