/* HomeKorAdminEducationTeacherActivity_SQL.selectHomeKorAdminEducationTeacherActivityList */ SELECT Z.* FROM ( SELECT ROWNUM AS RNUM, X.* FROM ( SELECT T1.no, T1.use_at, T1.type_ref, T1.year_ref, T1.region_ref, T1.title, T1.teacher_cnt, T1.student_cnt, T1.content, T2.data_name AS type_name, T3.data_name AS year_name, T4.data_name AS region_name, (SELECT FT.attach_name FROM cms_file_data FT AND FT.identifier = #{identifier} AND FT.key_ref = T1.no AND ROWNUM = 1) AS attach_name, (SELECT FT.attach_re_name FROM cms_file_data FT AND FT.identifier = #{identifier} AND FT.key_ref = T1.no AND ROWNUM = 1) AS attach_re_name, (SELECT FT.extension FROM cms_file_data FT AND FT.identifier = #{identifier} AND FT.key_ref = T1.no AND ROWNUM = 1) AS attach_extension, TO_CHAR(T1.reg_date, 'YYYY-MM-DD') AS reg_date, T1.register FROM tb_activity T1 LEFT OUTER JOIN cms_category_data T2 ON T1.type_ref = T2.no LEFT OUTER JOIN cms_category_data T3 ON T1.year_ref = T3.no LEFT OUTER JOIN cms_category_data T4 ON T1.region_ref = T4.no
AND T1.use_at = #{searchValue1}
AND T1.type_ref = #{searchValue2}
AND T1.year_ref = #{searchValue3}
AND T1.region_ref = #{searchValue4}
AND T1.title LIKE '%' || #{searchKeyword} || '%'
ORDER BY T1.reg_date DESC, T1.no DESC ) X ) Z
AND Z.RNUM > #{firstIndex} AND ROWNUM <= #{recordCountPerPage}

/* HomeKorAdminEducationTeacherActivity_SQL.selectHomeKorAdminEducationTeacherActivityListCnt */ SELECT COUNT(1) FROM tb_activity T1 LEFT OUTER JOIN cms_category_data T2 ON T1.type_ref = T2.no LEFT OUTER JOIN cms_category_data T3 ON T1.year_ref = T3.no LEFT OUTER JOIN cms_category_data T4 ON T1.region_ref = T4.no
