mapper.select:
/* HomeKorAdminEducationThemeInvention_SQL.selectHomeKorAdminEducationThemeInventionList */ SELECT Z.* FROM ( SELECT ROWNUM AS RNUM, X.* FROM ( SELECT T1.no, T1.use_at, T1.type_ref, T1.url, T1.title, T2.data_name AS type_name, TO_CHAR(T1.reg_date, 'YYYY-MM-DD') AS reg_date, T1.register FROM tb_invention T1 LEFT OUTER JOIN cms_category_data T2 ON T1.type_ref = T2.no
AND T1.use_at = #{searchValue1}
AND T1.type_ref = #{searchValue2}
AND T1.title LIKE '%' || #{searchKeyword} || '%'
ORDER BY T1.reg_date DESC, T1.no DESC ) X ) Z
AND Z.RNUM > #{firstIndex} AND ROWNUM <= #{recordCountPerPage}

/* HomeKorAdminEducationThemeInvention_SQL.selectHomeKorAdminEducationThemeInventionListCnt */ SELECT COUNT(1) FROM tb_invention T1 LEFT OUTER JOIN cms_category_data T2 ON T1.type_ref = T2.no
AND T1.use_at = #{searchValue1}
AND T1.type_ref = #{searchValue2}
AND T1.title LIKE '%' || #{searchKeyword} || '%'

