mapper.select:
/* HomeKorAdminHomepageFooter_SQL.selectHomeKorAdminHomepageFooterList */ SELECT Z.* FROM ( SELECT ROWNUM AS RNUM, X.* FROM ( SELECT T1.no, T1.use_at, T1.open_at, T1.title, T1.url, T1.sort, TO_CHAR(T1.reg_date, 'YYYY-MM-DD') AS reg_date, T1.register, TO_CHAR(T1.mod_date, 'YYYY-MM-DD') AS mod_date, T1.modifier FROM tb_footer T1
AND T1.use_at = #{searchValue1}
AND T1.title LIKE '%' || #{searchKeyword} || '%'
ORDER BY T1.reg_date DESC, T1.no DESC ) X ) Z
AND Z.RNUM > #{firstIndex} AND ROWNUM <= #{recordCountPerPage}

/* HomeKorAdminHomepageFooter_SQL.selectHomeKorAdminHomepageFooterListCnt */ SELECT COUNT(1) FROM tb_footer T1
AND T1.use_at = #{searchValue1}
AND T1.title LIKE '%' || #{searchKeyword} || '%'

