mapper.select:
/* HomeKorAdminHomepagePopup_SQL.selectHomeKorAdminHomepagePopupList */ SELECT Z.* FROM ( SELECT ROWNUM AS RNUM, X.* FROM ( SELECT T1.no, T1.use_at, T1.type_at, T1.open_at, T1.url, TO_CHAR(T1.reg_date, 'YYYY-MM-DD') AS reg_date, T1.register FROM tb_popup T1
AND T1.type_at = 'C'
ORDER BY T1.reg_date DESC, T1.no DESC ) X ) Z
AND Z.RNUM > #{firstIndex} AND ROWNUM <= #{recordCountPerPage}

/* HomeKorAdminHomepagePopup_SQL.selectHomeKorAdminHomepagePopupListCnt */
SELECT 
	COUNT(1)
FROM
	tb_popup T1

AND			T1.type_at															= #{searchValue1}

/* HomeKorAdminHomepagePopup_SQL.selectHomeKorAdminHomepagePopup */ SELECT T1.no, T1.use_at, T1.open_at, T1.type_at, T1.url, TO_CHAR(T1.reg_date, 'YYYY-MM-DD') AS reg_date, T1.register FROM tb_popup T1
AND T1.type_at = #{searchValue1} AND ROWNUM = 1
AND T1.no = #{idx}

mapper.insert:
/* HomeKorAdminHomepagePopup_SQL.insertHomeKorAdminHomepagePopup */
SELECT tb_popup_seq.NEXTVAL AS idx FROM dual
INSERT INTO tb_popup ( no, use_at, open_at, type_at, url, register, reg_date ) VALUES ( #{idx}, #{paramKey1}, #{paramKey2}, #{paramKey3}, #{paramKey4}, #{adm_idx}, SYSDATE )
