/* HomeKorUserEducationThemeStory_SQL.selectHomeKorUserEducationThemeStoryList */ SELECT Z.* FROM ( SELECT ROWNUM AS RNUM, X.* FROM ( SELECT T1.no, T1.use_at, T1.url, T1.title, TO_CHAR(T1.reg_date, 'YYYY-MM-DD') AS reg_date, CASE WHEN TO_NUMBER(TO_CHAR(T1.reg_date, 'YYYY')) - TO_NUMBER(TO_CHAR(SYSDATE, 'YYYY')) = 0 AND TO_NUMBER(TO_CHAR(T1.reg_date, 'MM')) - TO_NUMBER(TO_CHAR(SYSDATE, 'MM')) = 0 AND TO_NUMBER(TO_CHAR(SYSDATE, 'DD')) - TO_NUMBER(TO_CHAR(T1.reg_date, 'DD')) < 7 THEN 'Y' ELSE 'N' END AS new_at, TO_NUMBER(TO_CHAR(T1.reg_date, 'YYYY')) - TO_NUMBER(TO_CHAR(SYSDATE, 'YYYY')) AS year, TO_NUMBER(TO_CHAR(T1.reg_date, 'MM')) - TO_NUMBER(TO_CHAR(SYSDATE, 'MM')) AS month, TO_NUMBER(TO_CHAR(SYSDATE, 'DD')) - TO_NUMBER(TO_CHAR(T1.reg_date, 'DD')) AS day, T1.register, T2.name AS register_name FROM tb_story T1 LEFT OUTER JOIN site_account T2 ON T1.register = T2.no
AND T1.use_at = 'Y'
AND T1.title LIKE '%' || #{searchKeyword} || '%'
ORDER BY T1.reg_date DESC, T1.no DESC ) X ) Z
AND Z.RNUM > #{firstIndex} AND ROWNUM <= #{recordCountPerPage}

/* HomeKorUserEducationThemeStory_SQL.selectHomeKorUserEducationThemeStoryListCnt */ SELECT COUNT(1) FROM tb_story T1
AND T1.use_at = 'Y'
AND T1.title LIKE '%' || #{searchKeyword} || '%'
