mapper.select:
/* CmsRecordMng.selectRecordDataList */ SELECT Z.* FROM ( SELECT ROWNUM AS RNUM, X.* FROM ( SELECT T1.menu, T1.title, T1.down_reason, T1.work_code, T1.work_id, T1.work_name, T2.ip, TO_CHAR(T2.login_date, 'YYYY-MM-DD HH24:MI:SS') AS login_date, TO_CHAR(T1.perform_date, 'YYYY-MM-DD HH24:MI:SS') AS perform_date, T1.perform_date AS sort_date FROM cms_admin_record T1, cms_admin_access T2
ORDER BY sort_date DESC ) X ) Z
AND Z.RNUM > #{firstIndex} AND ROWNUM <= #{recordCountPerPage}
