mapper.select:
/* HomeKorAdminDirectionContact_SQL.selectHomeKorAdminDirectionContactList */ SELECT Z.* FROM ( SELECT ROWNUM AS RNUM, X.* FROM ( SELECT T1.no, T1.use_at, T1.page_at, T1.title, T1.content, T1.name, T1.email, T1.agreement, TO_CHAR(T1.reg_date, 'YYYY-MM-DD') AS reg_date, T1.register FROM tb_contact T1
AND T1.page_at = #{tabPos}
AND T1.use_at = #{searchValue1}
AND ( T1.name LIKE '%' || #{searchKeyword} || '%' OR T1.title LIKE '%' || #{searchKeyword} || '%' OR T1.content LIKE '%' || #{searchKeyword} || '%' )
AND T1.name LIKE '%' || #{searchKeyword} || '%'
AND T1.title LIKE '%' || #{searchKeyword} || '%'
AND T1.content LIKE '%' || #{searchKeyword} || '%'
ORDER BY T1.reg_date DESC, T1.no DESC ) X ) Z
AND Z.RNUM > #{firstIndex} AND ROWNUM <= #{recordCountPerPage}

/* HomeKorAdminDirectionContact_SQL.selectHomeKorAdminDirectionContactListCnt */ SELECT COUNT(1) FROM tb_contact T1
AND T1.page_at = #{tabPos}
AND T1.use_at = #{searchValue1}
