mapper.select:
/* HomeKorAdminHomepageWindow_SQL.selectHomeKorAdminHomepageWindowList */ SELECT Z.* FROM ( SELECT ROWNUM AS RNUM, X.* FROM ( SELECT T1.no, T1.use_at, T1.title, TO_CHAR(T1.start_date, 'YYYY-MM-DD') AS start_date, TO_CHAR(T1.end_date, 'YYYY-MM-DD') AS end_date, T1.horizontal_width, T1.vertical_width, T1.x_location, T1.y_location, T1.content, TO_CHAR(T1.reg_date, 'YYYY-MM-DD') AS reg_date, T1.register, TO_CHAR(T1.mod_date, 'YYYY-MM-DD') AS mod_date, T1.modifier FROM tb_window T1
AND T1.use_at = #{searchValue1}
AND T1.title LIKE '%' || #{searchKeyword} || '%'
ORDER BY T1.reg_date DESC, T1.no DESC ) X ) Z
AND Z.RNUM > #{firstIndex} AND ROWNUM <= #{recordCountPerPage}

/* HomeKorAdminHomepageWindow_SQL.selectHomeKorAdminHomepageWindowListCnt */ SELECT COUNT(1) FROM tb_window T1
AND T1.use_at = #{searchValue1}
AND T1.title LIKE '%' || #{searchKeyword} || '%'

mapper.insert:
