) VALUES ( #{idx}, #{paramKey1}, #{paramKey2}, TO_DATE(#{paramKey3}, 'YYYY-MM-DD'), TO_DATE(#{paramKey4}, 'YYYY-MM-DD'), #{paramKey5}, #{paramKey6}, #{paramKey7}, #{paramKey8}, #{paramKey9}, #{adm_idx}, SYSDATE )
