mapper.select:
/* HomeKorAdminMemberMngContact_SQL.selectContactDataList */ SELECT Z.* FROM ( SELECT ROWNUM AS RNUM, X.* FROM ( SELECT T1.tb_teacher_pool_seq AS no, T1.member_id, T1.member_name, T1.department, T1.department_position, T1.birth, T1.gender, T1.phone, T1.email, TO_CHAR(T1.create_date,'YYYY-MM-DD') AS create_date, T2.privacy_yn, T2.crims_yn FROM tb_teacher_pool T1 left join tb_teacher_agree T2 on t1.member_id = t2.member_id
AND ( T1.member_id LIKE '%' || #{searchKeyword} || '%' OR T1.member_name LIKE '%' || #{searchKeyword} || '%' )
AND T1.member_id LIKE '%' || #{searchKeyword} || '%'
AND T1.member_name LIKE '%' || #{searchKeyword} || '%'
ORDER BY T1.tb_teacher_pool_seq DESC ) X ) Z
AND Z.RNUM > #{firstIndex} AND ROWNUM <= #{recordCountPerPage}

