/* HomeKorUserCenterFindSQL.selectHomeKorUserCenterFindList */ SELECT Z.* FROM ( SELECT ROWNUM AS RNUM, X.* FROM ( SELECT T1.no, T1.use_at, T1.region_ref, (SELECT sub.data_name FROM cms_category_data sub WHERE sub.no = T1.region_ref) AS region_name, T1.title, T1.agency, T1.zipcode, T1.address, T1.address_detail, T1.latitute, T1.longitute, T1.phone, TO_CHAR(T1.reg_date, 'YYYY-MM-DD') AS reg_date, T1.register FROM tb_center_find T1
AND T1.use_at = 'Y'
AND T1.region_ref = #{searchValue1}
AND T1.title LIKE '%' || #{searchKeyword} || '%'
ORDER BY T1.reg_date DESC, T1.no DESC ) X ) Z
AND Z.RNUM > #{firstIndex} AND ROWNUM <= #{recordCountPerPage}

/* HomeKorUserCenterFindSQL.selectHomeKorUserCenterFindListCnt */ SELECT COUNT(1) FROM tb_center_find T1
AND T1.use_at = 'Y'
AND T1.region_ref = #{searchValue1}
AND T1.title LIKE '%' || #{searchKeyword} || '%'
