mapper.insert:
SELECT NVL(MAX(TB_TEACHER_CAREER_POOL_SEQ), 0) + 1 AS idx2 FROM tb_teacher_career_pool
/* insertTbTeacherCareerData */
INSERT INTO tb_teacher_career_pool( TB_TEACHER_CAREER_POOL_SEQ, TEACHER_POOL_REF, YEAR, PROGRAM, TURN, SORT, CREATE_DATE )VALUES( #{idx2}::integer, #{idx}, #{paramKey35}, #{paramKey36}, #{paramKey37}, #{paramKey42}, current_date )

SELECT NVL(MAX(TB_TEACHER_AGREE_SEQ), 0) + 1 AS idx FROM tb_teacher_agree
/* insertTbTeacherCareerData */
INSERT INTO tb_teacher_agree( TB_TEACHER_AGREE_SEQ, member_id, privacy_yn, privacy_dt, crims_yn, crims_dt )VALUES( #{idx}, #{paramKey1}, #{paramKey40}, now(), #{paramKey41}, now() )

mapper.update:
	/* updateTbTeacherData */
	UPDATE tb_teacher_agree set
		privacy_yn		=	#{paramKey40}
		,privacy_dt				= now()			
		,crims_yn		=	#{paramKey41}
		,crims_dt				= now()	
	
WHERE
		member_id = #{paramKey1}

mapper.delete:
/* deleteTeacherAgree */
	DELETE FROM tb_teacher_agree
	
	AND			member_id = #{paramKey1}

/* deleteTeacherData */
	UPDATE tb_teacher_pool set
	mod_date				= now(),
	leave_at = 'Y'
	
	AND			tb_teacher_pool_seq																= #{idx}

/* deleteTeacherCareerData */
	DELETE FROM tb_teacher_career_pool
	
	AND			teacher_pool_ref															= #{idx}

/* deleteTeacherEduData */
	DELETE FROM tb_teacher_edu_pool
	
	AND			teacher_pool_ref															= #{idx}
